//PRE OR POST PREDICLEMENTS, PLUS OCH MINUS
let numberOfStudents = 0;

numberOfStudents+= 1;
console.log(numberOfStudents); // 2

numberOfStudents++;
console.log(numberOfStudents); //1

//numberOfStudents--;
//console.log(numberOfStudents); //1

++numberOfStudents;
console.log(numberOfStudents); //3

console.log(numberOfStudents++) //3
console.log(numberOfStudents); //4
console.log(++numberOfStudents); // 5 :Position of ++ or -- is critical of which result you will get


